import React,{useState,useEffect, useContext} from "react";
import { AuthContext } from '../context/AuthContext'
import {useHttp} from "../hooks/http.hook" 
import {useMessage} from "../hooks/message.hook"
import {useHistory} from "react-router-dom"


function Login(params) {
    const history = useHistory()
    const auth = useContext(AuthContext)
    const message = useMessage()
    const {loading,error,request,clearError} = useHttp()
    const [form,setForm] = useState({
      login:'',
      password:''
    }) 

    useEffect(()=>{
   
        message(error)
        clearError()
      },[error,message,clearError])

     

      const changeHandler = e=>{
        console.log({...form, [e.target.name]: e.target.value})
        setForm({...form, [e.target.name]: e.target.value})
      }  

      const registerHandler= async ()=>{
        try{
          const data=await request('/api/auth/register','POST',{...form},{},"")
          message(data.message)
        }catch(e){
  
        }
    }
  
    const loginHandler= async ()=>{
      try{
        const data=await request('/api/auth/login','POST',{...form})
        auth.login(data.token,data.userId)
        //history.push(`/admpage`)
      }catch(e){
  
      }
  }

    function submitHandler(event){
        event.preventDefault()
    }
    return(
        <>
            <div className="container ">
                <h4>Login page</h4>
                <form className="login-form" onSubmit={submitHandler}>
                    <div className="row ">
                    <div class="input-field col s12 m5 offset-m3">
                        <input value={form.login} onChange={changeHandler} id="login" name="login"  type="text" />
                        <label for="login">Login</label>
                     </div>
                    <div class="input-field col s12 m5 offset-m3">
                        <input value={form.password} onChange={changeHandler} id="password" name="password" type="password" />
                        <label for="password">Password</label>
                     </div>
                     <div className="col s6 m1 offset-m5 offset-s4">
                        <button  disabled={loading} onClick={loginHandler} className="btn waves-effect waves-light red darken-3" type="submit">Login</button>
                     </div>
                     <div className="col s6 m1 offset-m5 offset-s4">
                        <button  disabled={loading} onClick={registerHandler} className="btn waves-effect waves-light red darken-3" type="submit">Register</button>
                     </div>
                    </div>
                </form>
            </div>
        </>
    )
}

export default Login
