import React , {useCallback, useContext, useEffect, useState} from "react";
import GalleryCard from "../components/GalleryCard";
import {useHttp} from '../hooks/http.hook'
import {Loader} from '../components/Loader'

function  GalleryPage() {
    const {request, loading} = useHttp()
    const [categories, setCategories] = useState([])


    const fetchCategories = useCallback(async () => {
        try {
            const fetched = await request('/api/galleryCategory-edit/gallery-categories', 'GET', null)
            setCategories(fetched)
            console.log(fetched)
    } catch (e) {}
  }, [request])
  
  useEffect(() => {
    fetchCategories()
  }, [fetchCategories])

  if (loading) {
    return <Loader/>
  }
    return(

        <>
            <div className="container">
                <div className="row">
                   <GalleryCard pathIMG="https://images.unsplash.com/photo-1614795049603-74ed8d9949d0?ixid=
                   MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1
                   &auto=format&fit=crop&w=634&q=80" title="Title" 
                   text="Here is some more information about 
                   this product that is only revealed once clicked on." link="/gallery/id"/>
                  
                </div>
            </div>
        </>
    )
    
}

export default GalleryPage
