import React , {useCallback, useContext, useEffect, useState} from "react";
import {useParams} from 'react-router-dom'
import {useHttp} from '../hooks/http.hook'
import {Loader} from '../components/Loader'




function ArticleContent(props) {

    const {request, loading} = useHttp()
    const [article, setArticle] = useState(null)
    const articleId = useParams().id


    const getArticle = useCallback(async () => {
        try {
          const fetched = await request(`/api/article-edit/${articleId}`, 'GET', null, )
          setArticle(fetched)
        } catch (e) {}
      }, [articleId , request])
    
      useEffect(() => {
        getArticle()
      }, [getArticle])
    
      if (loading) {
        return <Loader />
      }

    return(
        <>
        <div className="container">
            <h2>{art}</h2>
            <div className="img-article hoverable">
                <img className="responsive-img" src="https://images.unsplash.com/photo-1606297158300-5c4f6d171be9?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80" alt="img"/>
            </div>
            <div className="flow-text">

            </div>
        </div>
        </>
    )
}

export default ArticleContent
