import React from 'react'


function Upload(){
    function submitHandler(event){
        event.preventDefault()
    }

    const [files,setFiles] = useState()

    
    const handleImageChange= e=> {
        e.preventDefault();
     
         const file = e.target.files
         
         setFile(files)

         console.log(file)
         
       }

    const updateHandler= async ()=>{
        try{
          const dataForm = new FormData()
          for (const f of file){
            dataForm.append("files", file)
          }
       
          const data=await request(`/api/articles-edit/update/${articleId}`,'POST',{...form} , 
          {Authorization: `Bearer ${auth.token}`},'application/json')
          message(data.message)
          console.log(data)
            if (file !== undefined){
                const imgdata = await request('/api/articles-edit/upload-img','POST',dataForm, 
                {},)
                message(imgdata.message)
            }
        
          
        }catch(e){
  
        }
    }
    return(
        <>
             <div className="row">
            
            <div className="adm-form col s12 m12">
           
                <form onSubmit={submitHandler} method="post" enctype="multipart/form-data">
               
                <div class="row">
                <h4 className="col s8 offset-s4">Upload</h4>
               
                    
                   
                    <div className="input-field label-file-input col s3 offset-s2  m6 offset-m5  l1 offset-l4">
                        <label for="file">Image</label>
                      
                    </div>
                    <div className="input-field label-file-input col s2 m6 offset-m5  l4">
                     
                        <input type="files" id="files" name="files" multiple/>
                    </div>
                     </div>
                     <div className="row">
                    <div  className="input-field col s3 offset-s3  m2 offset-m4  l1 offset-l4  btn-form">
                        <button type="submit" className="btn waves-effect waves-light">Upload</button>
                    </div>
                   
                           
                    </div>
                </form>               
            </div>
        </div>
        </>
    )
}
export default Upload
