import React,{useEffect, useContext,useState} from "react";
import {Link} from 'react-router-dom'
import GalleryCategoryFormFields from "./GalleryCategoryFormFields"
import { AuthContext } from '../context/AuthContext'
import {useHttp} from "../hooks/http.hook" 
import {useMessage} from "../hooks/message.hook"

function GalleryCategoryForm(){
    function submitHandler(event){
        event.preventDefault()
    }

    const auth = useContext(AuthContext)
    const message = useMessage()
    const {loading,error,request,clearError} = useHttp()
    const [form,setForm] = useState({
      title:'',
      description:'',
      
    }) 
    const [file,setFile] = useState()

      

        useEffect(()=>{
            message(error)
            clearError()
          },[error,message,clearError])

          const changeHandler = e=>{
            console.log(form)
            setForm({...form, [e.target.name]: e.target.value})
          }  

          const handleImageChange= e=> {
           e.preventDefault();
        
            const file = e.target.files[0]
            setFile(file)

            console.log(file)
            //reader.readAsDataURL(file)
          }
    
          const createHandler= async ()=>{
            try{
              const dataForm = new FormData()
              dataForm.append("file", file)
              const data=await request('/api/articles-edit/create','POST',{...form} , 
              {Authorization: `Bearer ${auth.token}`},'application/json')
              message(data.message)
              console.log(data)

              const imgdata = await request('/api/articles-edit/upload-img','POST',dataForm, 
              {},)
              message(imgdata.message)
              
            }catch(e){
      
            }
        }
    return(

        <>
         <div className="row">
            
            <div className="adm-form col s12 m12">
           
                <form onSubmit={submitHandler} method="post" enctype="multipart/form-data">
               
                        <GalleryCategoryFormFields title="Create category gallery"/>
                     <div className="row">
                    <div  className="input-field col s3 offset-s3  m2 offset-m4  l1 offset-l4  btn-form">
                        <button type="submit" className="btn waves-effect waves-light">Create</button>
                    </div>
                    <div  className="input-field col s4   m1  l1  btn-form">
                        <Link to="/admpage/galleryCategory-update" className="btn blue waves-effect waves-light">Update</Link>
                    </div>
                           
                    </div>
                </form>               
            </div>
        </div>
        </>
    )


}

 export default GalleryCategoryForm;
