import React,{useEffect} from 'react'
import SliderItem from './SliderItem'
import M from "materialize-css"
import {Loader} from '../components/Loader'
import {useHttp} from '../hooks/http.hook'

function Slider(props){
    const {request, loading} = useHttp()
    useEffect(()=>{
    
        var elems = document.querySelectorAll('.slider');
        var instances = M.Slider.init(elems, {});
      
    },[])
    if (loading) {
        return <Loader/>
      }
    return(
        <>
        <div className="container">
            <div className="slider hoverable">
                <ul className="slides">
                {!loading && articles.map((article,index)=>{
                    return(
                      <ArticlesItem pathIMG={`/images/articles/${article.img}`} title={article.title} 
                      text={article.markdown} 
                      link={`/articles/${article._id}`}
                      linkAdm={`/admpage/article-update/${article._id}`}/>
                    )
                  })}
                    <SliderItem
                     pathIMG="/images/resort1.jpg"
                     textTitle="This is our big Tagline"
                     text="Here's our small slogan1"
                     />
                     <SliderItem
                     pathIMG="/images/resort2.jpg"
                     textTitle="Left Aligned Caption"
                     text="Here's our small slogan2"
                     />
                     <SliderItem
                     pathIMG="/images/resort3.jpg"
                     textTitle="Right Aligned Caption"
                     text="Here's our small slogan3"
                     />
                   
                </ul>

            </div>
            </div>
        </>
    )
}
export default Slider
