import React,{useEffect, useState,useCallback,useContext} from 'react'
import Slider from "../components/Slider"
import IconBoxes from "../components/IconBoxes"
import FollowSection from "../components/FollowSection"
import Gallery from "../components/Gallery"
import {useParams} from 'react-router-dom'
import {useHttp} from '../hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import {Loader} from '../components/Loader'


function Home(){
     const auth = useContext(AuthContext)
    const {request, loading} = useHttp()
    const [images,setImages] = useState([])

    const [articles, setArticles] = useState([])
    const [form,setForm] = useState({
      title:'',
      address:'',
      phone:'',
      phoneAdditional:'',
      email:'',
      _id:'',
      city:'',
      map:'',
      vk:'',
      instagram:'',
      facebook:'',
      telegram:''
    })

    const fetchArticles = useCallback(async () => {
        try {
          const limit = 5  
          const fetched = await request('/api/articles-edit/articles-limit', 'POST', {limit:limit},
          {},'application/json')
          setArticles(fetched)
          console.log(fetched)
        } catch (e) {}
      }, [request])
      useEffect(() => {
        fetchArticles()
       
      }, [fetchArticles])

      const fetchOrg = useCallback(async () => {
        try {
            const fetched = await request('/api/organization-edit/organization', 
            'POST', {...form},{},'application/json')
            
            setForm({...fetched})
            console.log(fetched)
           
    } catch (e) {}
  }, [request])

      if (loading) {
        return <Loader/>
      }

    return(
        <>
        
        <Slider articles={articles}/>
        <IconBoxes/>
        <FollowSection/>
        <Gallery additionalClass={"center"} images={images} title="Фото галерея"/>
        
        </>

    )
}

export default Home;
