import React , {useCallback, useContext, useEffect, useState} from 'react'
import ArticlesItem from '../components/ArticleItem'

import {useParams} from 'react-router-dom'
import {useHttp} from '../hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import {Loader} from '../components/Loader'


function Articles(){
  const {request, loading} = useHttp()
  const [articles, setArticles] = useState([])
  const [currentPage,setCurrentPage]  = useState(1)
  const [countArticles,setCountArticles] = useState(2)


  const fetchArticles = useCallback(async () => {
    try {
      const fetched = await request('/api/articles-edit/articles', 'GET', null)
      setArticles(fetched)
      console.log(fetched)
    } catch (e) {}
  }, [request])
  
  useEffect(() => {
    fetchArticles()
  }, [fetchArticles])

  const indexLastPage = currentPage * countArticles
  const indexFirstArticle = indexLastPage - countArticles
  const currentArticles = articles.slice(indexFirstArticle,index)

  if (loading) {
    return <Loader/>
  }
    return(
        <>
            <div className="container">
                <div className="row">
                  {!loading && articles.map((article,index)=>{
                    return(
                      <ArticlesItem pathIMG={`/images/articles/${article.img}`} title={article.title} 
                      text={article.markdown} 
                      link={`/articles/${article._id}`}
                      linkAdm={`/admpage/article-update/${article._id}`}/>
                    )
                  })}
                       
                        
                  <div className="row">
                    <div className="col s12">
                    <ul className="pagination">
                        <li className="waves-effect"><a href="#!"><i class="material-icons">chevron_left</i></a></li>
                        <li className="waves-effect"><a href="#!">1</a></li>
                         <li className="waves-effect"><a href="#!">2</a></li>
                         <li className="waves-effect"><a href="#!">3</a></li>
                        <li className="waves-effect"><a href="#!">4</a></li>
                        <li className="waves-effect"><a href="#!">5</a></li>
                        <li className="waves-effect"><a href="#!"><i class="material-icons">chevron_right</i></a></li>
                      </ul>
                    </div>
                      
                  </div>
                </div>
            </div>
        </>
    )
}

export default Articles
