import React,{useContext} from "react";
import NavbarNavigationItem from "./NavbarNavigationItem";
import {NavLink, useHistory} from 'react-router-dom'
import { AuthContext } from '../context/AuthContext'

function NavbarNavigation(props){
    const auth = useContext(AuthContext)
    const history = useHistory()

    const logoutHandler = event => {
        event.preventDefault()
        auth.logout()
        history.push('/')
      }


     if (auth.isAuthenticated){
         return(
            <>
            <ul className={props.class} id={props.id}>
            <NavbarNavigationItem classNew="linkNav" link="/" click={logoutHandler} title="Выход" class={props.classChild} classActive={props.classActiveChild}/>
                <NavbarNavigationItem link="/news" title="Новости" class={props.classChild} classActive={props.classActiveChild}/>
                <NavbarNavigationItem link="/gallery" title="Галерея" class={props.classChild} classActive={props.classActiveChild}/>
                <NavbarNavigationItem link="/articles" title="Статьи" class={props.classChild} classActive={props.classActiveChild}/>
                            
             </ul>
            </> 
         )
     }

    return(
        <>
        <ul className={props.class} id={props.id}>
            <NavbarNavigationItem link="/news" title="News" class={props.classChild} classActive={props.classActiveChild}/>
            <NavbarNavigationItem link="/gallery" title="Gallery" class={props.classChild} classActive={props.classActiveChild}/>
            <NavbarNavigationItem link="/articles" title="Article" class={props.classChild} classActive={props.classActiveChild}/>
                        
         </ul>
        </>

    )
}

export default NavbarNavigation
