import React , {useCallback, useContext, useEffect, useState} from "react";
import {useParams} from 'react-router-dom'
import {useHttp} from '../hooks/http.hook'
import {Loader} from '../components/Loader'
import {AuthContext} from '../context/AuthContext'
import ArticleContentItem from "../components/ArticleContentItem";




function ArticleContent() {

    const {request, loading} = useHttp()
    const [article, setArticle] = useState()
    const articleId = useParams().id


    const getArticle = useCallback(async () => {
        try {
          const fetched = await request(`/api/articles-edit/${articleId}`, 'GET', null)
          setArticle(fetched)
          
        } catch (e) {console.log(e)}
      }, [articleId , request])
    
      useEffect(() => {
        getArticle()
      }, [getArticle])
    
      if (loading) {
        return <Loader />
      }
     
        return(
            <>
           {!loading && <ArticleContentItem/>}
            {articleId}
            {/* <div className="container">
                <h2>{article.title}</h2>
                <div className="img-article hoverable">
                    <img className="responsive-img" src={`/images/articles/${article.img}`} alt="img"/>
                </div>
                <div className="flow-text">
                    {article.sanitizedHtml}
                </div>
            </div> */}
            </>
        )
      

   
}

export default ArticleContent
