import React , {useCallback, useContext, useEffect, useState} from "react";
import GalleryCard from "../components/GalleryCard";
import Pagination from '../components/Pagination'
import {useHttp} from '../hooks/http.hook'
import {Loader} from '../components/Loader'

function  GalleryPage() {
    const {request, loading} = useHttp()
    const [categories, setCategories] = useState([])
    const [currentPage,setCurrentPage]  = useState(1)
    const [countCategories,setCountCategories] = useState(2)
    const [countPage,setCountPage] = useState(1)


    const fetchCategories = useCallback(async () => {
        try {
            const fetched = await request('/api/galleryCategory-edit/gallery-categories', 'GET', null)
            setCategories(fetched)
            console.log(fetched)
    } catch (e) {}
  }, [request])
  
  useEffect(() => {
    fetchCategories()
  }, [fetchCategories])

  const indexLastArticle = currentPage * countArticles
  const indexFirstArticle = indexLastArticle - countArticles
  const currentArticles = articles.slice(indexFirstArticle,indexLastArticle)

  const paginate=(pageNumber)=>{setCurrentPage(pageNumber)}
  const paginatePrev=(pageNumber)=>{
    if (currentPage!==1){
      setCurrentPage(pageNumber-1)
      console.log(currentPage)
    }
 
  }

   
  const paginateNext= (pageNumber,lastIndex) => {
    if (pageNumber!==lastIndex){
      setCurrentPage(pageNumber+1)
    }
  }

  if (loading) {
    return <Loader/>
  }
    return(

        <>
            <div className="container">
                <div className="row">
                  
                   {!loading && categories.map((category,index)=>{
                    return(
                      <GalleryCard pathIMG={`/images/galleryCategory/${category.img}`} title={category.title} 
                      description={category.description} 
                      link={`/gallery/${category._id}`}
                      linkAdm={`/admpage/galleryCategory-update/${category._id}`}/>
                    )
                  })}
                </div>
            </div>
        </>
    )
    
}

export default GalleryPage
