import React , {useCallback, useContext, useEffect, useState} from "react";
import GalleryImageFormFields from "./GalleryImageFormFields";
import {NavLink,Link} from "react-router-dom";
import {useHttp} from '../hooks/http.hook'
import {Loader} from '../components/Loader'
import {useMessage} from "../hooks/message.hook"

function GalleryImageFormAdd(){
    function submitHandler(event){
        event.preventDefault()
    }
   
    const {request, loading} = useHttp()
    const message = useMessage()
    const [form,setForm] = useState({id:''})
    const [categories, setCategories] = useState([])
    const [searchTerm,setSearchTerm] = useState('')
    const [files,setFiles] = useState()
    

    const handleSelect=(e)=>{
      
        setForm({...form,id:e.target.value})
    }
      
    const handleImageChange= e=> {
        e.preventDefault();
     
         const files = e.target.files
         
         setFiles(files)

         
         
       }

    const handleSearch=(e)=>{
        setSearchTerm(e.target.value)
        console.log(searchTerm)
    }

    const fetchCategories = useCallback(async () => {
        try {
            const fetched = await request('/api/galleryCategory-edit/gallery-categories', 'GET', null)
            setCategories(fetched)
            console.log(fetched)
           
    } catch (e) {}
  }, [request])
  
  useEffect(() => {
    fetchCategories()
    
  }, [fetchCategories])

  if (loading) {
    return <Loader/>
  }

 
  const createImg = async()=>{
    try{
        const dataForm = new FormData()
        for (const f of files){
          dataForm.append("files", f)
        }
        const data=await request(`/api/galleryCategory-edit/add-img-create`,'POST',{...form} , 
        {},'application/json')
         message(data.message)
        const images = await request(`/api/galleryCategory-edit/add-img`,'POST',dataForm, 
        {},)
        message(images.message)
    }catch(e){}
}

const check = ()=>{
    console.log(form)
}

    return(
        <>
             <div className="row">
            
            <div className="adm-form col s12 m12">
           
                <form onSubmit={submitHandler} method="post" enctype="multipart/form-data">
               
                        <GalleryImageFormFields 
                        handleSearch={handleSearch} 
                        handleSelect={handleSelect} 
                        categories={categories} 
                        stateSearch={searchTerm}
                        handleImageChange={handleImageChange}
                        title="Add image"/>
                     <div className="row">
                    <div  className="input-field col s3 offset-s3  m2 offset-m4  l1 offset-l4  btn-form">
                        <button onClick={createImg} type="submit" className="btn waves-effect waves-light">Create</button>
                    </div>
                    <div  className="input-field col s3 offset-s3  m2 offset-m4  l1 offset-l4  btn-form">
                        <button onClick={check} type="submit" className="btn waves-effect waves-light">Check</button>
                    </div>
                    <div  className="input-field col s4   m1  l1  btn-form">
                        <Link to="/admpage/galleryCategory-update" className="btn blue waves-effect waves-light disabled">Update</Link>
                    </div>
                           
                    </div>
                </form>               
            </div>
        </div>
        </>
    )
}

export default GalleryImageFormAdd
