import React from 'react'
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from "./components/Footer"
import Loader from "./components/Loader"
import Home from "./Pages/Home"
import Articles from './Pages/Articles'
import "./materialize.css"
import './App.css';
import ArticleContent from './Pages/ArticleContent';
import GalleryPage from './Pages/GalleryPage';
import GalleryContent from './Pages/GalleryContent';
import { AuthContext } from './context/AuthContext';
import {useAuth} from "./hooks/auth.hook"
import Login from './Pages/Login';

import {useRoutesAdm} from "./routesAdm";

function App() {
  const {token,login,logout,userId,ready} = useAuth() 
    const isAuthenticated=!!token
    const routes = useRoutesAdm(isAuthenticated)

    if(!ready){
      return <Loader/>
  }

  return (
    <AuthContext.Provider value={
      {
          token,login,logout,userId,isAuthenticated
      }
  }>
    <>
     <main>
      <Router>
        <Navbar />
        <Switch>
           <Route path='/' exact component={Home} />
        </Switch>
        <Switch>
          <Route path="/articles" exact component={Articles}/>
          <Route path="/articles/id" exact component={ArticleContent}/>
        </Switch>

       <Switch>
         <Route path="/gallery" exact component={GalleryPage}/>
         <Route path="/gallery/id" exact component={GalleryContent}/>
       </Switch>
        {routes}
       <Switch>
         <Route path="/login" exact component={Login}/>
       </Switch>


      </Router>
      </main>
      <Footer/>

    </>
    </AuthContext.Provider>
  );
}

export default App;
