import React,{useEffect,useCallback,useState,useContext} from 'react'
import M from 'materialize-css'
import { AuthContext } from '../context/AuthContext'
import {Loader} from '../components/Loader'
import {useHttp} from '../hooks/http.hook'

function Documents(){
    const auth = useContext(AuthContext)
    const {request, loading} = useHttp()
    const [categories, setCategories] = useState([])
    const [files, setFiles] = useState([])

    const fetchCategories = useCallback(async () => {
        try {
          const fetched = await request('/api/documents-edit/documents-categories', 'GET', null)
          setCategories(fetched)
          fetched = await request('/api/documents-edit/files-all', 'GET', null)
          setFiles(fetched)
        } catch (e) {}
      }, [request])
      
      useEffect(() => {
        fetchCategories()
      }, [fetchCategories])


    useEffect(()=>{
        
        var elem = document.querySelector('.collapsible.expandable');
        var instance = M.Collapsible.init(elem, {
        accordion: false
});
    
    if (loading) {
    return <Loader/>
  }
            
    })
    return(
        <>
        <div className="container">
            <div className="row">
                <div className="col s12">
                    {/* <a href="/images/articles/text.txt" rel="noopener" download>ssss</a> */}
                    <h3>Документы</h3>
                    <ul class="collapsible expandable">
                    {!loading && categories.map((category,index)=>{
                    return(
                     <li>
                         <div class="collapsible-header">{category.title}</div>
                         {files.filter((val)=>{
                        if (val===category._id){
                            return val
                        } else if (val.title.toLowerCase().includes(props.stateSearch.toLowerCase())){
                            return val
                        }
                    }).map((category,index)=>{
                        return(<option key={index} value={category._id}>{category.title}</option>)
                  })}
                            <div class="collapsible-body"><span>Lorem ipsum dolor sit amet.</span></div>
                     </li>
                    )
                  })}

                        <li>
                            <div class="collapsible-header">First</div>
                            <div class="collapsible-body"><span>Lorem ipsum dolor sit amet.</span></div>
                        </li>
                        <li>
                            <div class="collapsible-header">Second</div>
                            <div class="collapsible-body"><span>Lorem ipsum dolor sit amet.</span></div>
                        </li>
                        <li>
                            <div class="collapsible-header">Third</div>
                            <div class="collapsible-body"><span>Lorem ipsum dolor sit amet.</span></div>
                    </li>
                </ul>
                </div>
            </div>
           
        </div>
        </>
    )
}

export default Documents
