import React , {useCallback, useContext, useEffect, useState} from "react";
import GalleryImageFormFields from "./GalleryImageFormFields";
import {NavLink,Link} from "react-router-dom";
import {useHttp} from '../hooks/http.hook'
import {Loader} from '../components/Loader'

function GalleryImageFormAdd(){
    function submitHandler(event){
        event.preventDefault()
    }
    const {request, loading} = useHttp()
    const [categories, setCategories] = useState([])


    const fetchCategories = useCallback(async () => {
        try {
            const fetched = await request('/api/galleryCategory-edit/gallery-categories', 'GET', null)
            setCategories(fetched)
           
    } catch (e) {}
  }, [request])
  
  useEffect(async() => {
   
  }, [fetchCategories])

  if (loading) {
    return <Loader/>
  }

    return(
        <>
             <div className="row">
            
            <div className="adm-form col s12 m12">
           
                <form onSubmit={submitHandler} method="post" enctype="multipart/form-data">
               
                        <GalleryImageFormFields categories={categories} title="Add image"/>
                     <div className="row">
                    <div  className="input-field col s3 offset-s3  m2 offset-m4  l1 offset-l4  btn-form">
                        <button type="submit" className="btn waves-effect waves-light">Create</button>
                    </div>
                    <div  className="input-field col s4   m1  l1  btn-form">
                        <Link to="/admpage/galleryCategory-update" className="btn blue waves-effect waves-light">Update</Link>
                    </div>
                           
                    </div>
                </form>               
            </div>
        </div>
        </>
    )
}

export default GalleryImageFormAdd
