import React,{useEffect,useContext,useState} from "react";
import {Link} from 'react-router-dom'
import ArticleFormFields from "./ArticleFormFields"
import { AuthContext } from '../context/AuthContext'
import {useHttp} from "../hooks/http.hook" 
import {useMessage} from "../hooks/message.hook"

function ArticleForm(){

    const auth = useContext(AuthContext)
    const message = useMessage()
    const {loading,error,request,clearError} = useHttp()
    const [form,setForm] = useState({
      login:'',
      password:''
    }) 
        function submitHandler(event){
            event.preventDefault()
        }

        useEffect(()=>{
   
            message(error)
            clearError()
          },[error,message,clearError])
    return(
        
        <>
        <div className="row">
            
            <div className="adm-form col s12 m12">
           
                <form onSubmit={submitHandler} method="post" enctype="multipart/form-data">
               
                        <ArticleFormFields title="Create Article"/>
                     <div className="row">
                    <div  className="input-field col s3 offset-s3  m2 offset-m4  l1 offset-l4  btn-form">
                        <button type="submit" className="btn waves-effect waves-light">Create</button>
                    </div>
                    <div  className="input-field col s4   m1  l1  btn-form">
                        <Link to="/admpage/article-update" className="btn blue waves-effect waves-light">Update</Link>
                    </div>
                           
                    </div>
                </form>               
            </div>
        </div>
           
                    
        </>
   
            
        

    )

}

export default ArticleForm
